for k in a:
  z = a[n:n+13] #coge los dígitos con índice de n a n+13
  for i in z: #de la string z, multiplica y suma y a cada índice de z
    y *= int(z[j])
    j += 1
  z = 0 #reiniciamos el valor de cada variable tras el segundo for loop
